import React from "react";
import { handleUseParams, IRouterProvider } from "@pankod/refine-core";
import {
    useLocation,
    useParams,
    Link,
    RouteProps,
    BrowserRouterProps,
    useNavigate,
} from "react-router-dom";

import {
    BrowserRouterComponent,
    MemoryRouterComponent,
    HashRouterComponent,
} from "./routerComponent";
import { Prompt } from "./prompt";

export type RefineRouteProps = RouteProps & {
    layout?: boolean;
};
interface IReactRouterProvider extends IRouterProvider {
    useLocation: typeof useLocation;
    Link: typeof Link;
    useParams: <
        Params extends { [K in keyof Params]?: string } = {},
    >() => Params;
    RouterComponent: React.FC<BrowserRouterProps>;
    routes?: RefineRouteProps[];
}

const RouterProvider: IReactRouterProvider = {
    useHistory: () => {
        const navigate = useNavigate();

        return {
            push: navigate,
            replace: (path: string) => {
                navigate(path, { replace: true });
            },
            goBack: () => {
                navigate(-1);
            },
        };
    },
    useLocation,
    useParams: () => {
        const params = useParams();
        const { pathname } = useLocation();

        const paramsString = `/${Object.values(params).join("/")}`;
        return handleUseParams({
            ...params,
            resource:
                Object.keys(params).length === 0
                    ? pathname.substring(1)
                    : decodeURIComponent(pathname.substring(1)).replace(
                          paramsString,
                          "",
                      ),
        });
    },
    Prompt: Prompt as any,
    Link,
    RouterComponent: BrowserRouterComponent,
};

export * from "react-router-dom";

export default RouterProvider;
export { MemoryRouterComponent, HashRouterComponent, BrowserRouterComponent };
import React from "react";
import {
    BrowserRouter,
    BrowserRouterProps,
    MemoryRouter,
    MemoryRouterProps,
    HashRouter,
    HashRouterProps,
} from "react-router-dom";

import { RouteProvider } from "./routeProvider";

export function BrowserRouterComponent(
    this: { initialRoute?: string },
    { children, ...props }: BrowserRouterProps,
): JSX.Element {
    return (
        <BrowserRouter {...props}>
            <RouteProvider
                initialRoute={
                    typeof this !== "undefined" ? this.initialRoute : undefined
                }
            />
            {children}
        </BrowserRouter>
    );
}

export function MemoryRouterComponent(
    this: { initialRoute?: string },
    { children, ...props }: MemoryRouterProps,
): JSX.Element {
    return (
        <MemoryRouter {...props}>
            <RouteProvider
                initialRoute={
                    typeof this !== "undefined" ? this.initialRoute : undefined
                }
            />
            {children}
        </MemoryRouter>
    );
}

export function HashRouterComponent(
    this: { initialRoute?: string },
    { children, ...props }: HashRouterProps,
): JSX.Element {
    return (
        <HashRouter {...props}>
            <RouteProvider
                initialRoute={
                    typeof this !== "undefined" ? this.initialRoute : undefined
                }
            />
            {children}
        </HashRouter>
    );
}
