export * from "@tanstack/react-query";

export * from "./components";
export * from "./hooks";

export {
    Action,
    ActionWithPage,
    AccessControlProvider,
    AuditLogProvider,
    AuthProvider,
    BaseKey,
    BaseRecord,
    CanParams,
    CanReturnType,
    ConditionalFilter,
    CreateManyResponse,
    CreateResponse,
    CrudFilter,
    CrudFilters,
    CrudOperators,
    CrudSort,
    CrudSorting,
    CustomResponse,
    DeleteManyResponse,
    DeleteOneResponse,
    FormAction,
    GetListResponse,
    GetManyResponse,
    GetOneResponse,
    HttpError,
    LayoutProps,
    LiveEvent,
    LiveModeProps,
    LogicalFilter,
    LogParams,
    MapDataFn,
    MetaDataQuery,
    MutationMode,
    NotificationProvider,
    OpenNotificationParams,
    Option,
    Pagination,
    PromptProps,
    RedirectionTypes,
    RedirectAction,
    ResourceErrorRouterParams,
    ResourceProps,
    ResourceRouterParams,
    RouteAction,
    SortOrder,
    SuccessErrorNotification,
    TitleProps,
    TranslationProvider,
    UpdateManyResponse,
    UpdateResponse,
    I18nProvider,
    IAccessControlContext,
    IAuthContext,
    IDataContextProvider as DataProvider,
    ILiveContext as LiveProvider,
    ILog,
    ILogData,
    ILoginForm,
    INotificationContext,
    IResourceComponents,
    IResourceComponentsProps,
    IResourceContext as ResourceProvider,
    ITranslationContext,
    IResourceItem,
    IRouterProvider,
    ITreeMenu,
    IQueryKeys,
} from "./interfaces";

// all auth types
export * from "./interfaces/auth";

export {
    getDefaultFilter,
    getDefaultSortOrder,
    parseTableParams,
    parseTableParamsFromQuery,
    setInitialFilters,
    setInitialSorters,
    stringifyTableParams,
    unionFilters,
    unionSorters,
} from "./definitions/table";
export {
    createTreeView,
    handleUseParams,
    importCSVMapper,
    routeGenerator,
    userFriendlyResourceName,
    getNextPageParam,
    getPreviousPageParam,
} from "./definitions/helpers";
export { file2Base64 } from "./definitions/upload";
import React, { useEffect, useState } from "react";

import {
    useNavigation,
    useTranslate,
    useResourceWithRoute,
    useRouterContext,
} from "@hooks";
import { ResourceErrorRouterParams, ActionWithPage } from "../../../interfaces";

/**
 * When the app is navigated to a non-existent route, refine shows a default error page.
 * A custom error component can be used for this error page.
 *
 * @see {@link https://refine.dev/docs/api-references/components/refine-config#catchall} for more details.
 */
export const ErrorComponent: React.FC = () => {
    const [errorMessage, setErrorMessage] = useState<string>();
    const { push } = useNavigation();
    const translate = useTranslate();
    const actionTypes: ActionWithPage[] = ["edit", "create", "show"];

    const { useParams } = useRouterContext();

    const params = useParams<ResourceErrorRouterParams>();
    const resource = useResourceWithRoute();

    useEffect(() => {
        if (params.resource) {
            const resourceFromRoute = resource(params.resource);
            if (
                params.action &&
                actionTypes.includes(params.action) &&
                !resourceFromRoute[params.action]
            ) {
                setErrorMessage(
                    translate(
                        "pages.error.info",
                        {
                            action: params.action,
                            resource: params.resource,
                        },
                        `You may have forgotten to add the "${params.action}" component to "${params.resource}" resource.`,
                    ),
                );
            }
        }
    }, [params]);

    return (
        <>
            <h1>
                {translate(
                    "pages.error.404",
                    undefined,
                    "Sorry, the page you visited does not exist.",
                )}
            </h1>
            {errorMessage && <p>{errorMessage}</p>}
            <button onClick={() => push("/")}>
                {translate("pages.error.backHome", undefined, "Back Home")}
            </button>
        </>
    );
};
import { useContext } from "react";

import { AuthContext } from "@contexts/auth";
import { IAuthContext } from "../../../interfaces";
import {
    useQuery,
    UseQueryResult,
    UseQueryOptions,
} from "@tanstack/react-query";

/**
 * `usePermissions` calls the `getPermissions` method from the {@link https://refine.dev/docs/core/providers/auth-provider `authProvider`} under the hood.
 *
 * @see {@link https://refine.dev/docs/core/hooks/auth/usePermissions} for more details.
 *
 * @typeParam TData - Result data of the query
 *
 */
export const usePermissions = <TData = any>(
    options?: UseQueryOptions<TData>,
): UseQueryResult<TData, unknown> => {
    const { getPermissions } = useContext<IAuthContext>(AuthContext);

    const queryResponse = useQuery<TData>(
        ["usePermissions"],
        // Enabled check for `getPermissions` is enough to be sure that it's defined in the query function but TS is not smart enough to know that.
        getPermissions ?? (() => Promise.resolve(undefined)),
        {
            enabled: !!getPermissions,
            ...options,
        },
    );

    return queryResponse;
};
import React from "react";
import { useQueryClient } from "@tanstack/react-query";

import { useNavigation } from "@hooks";
import { IAuthContext } from "../../interfaces";

export const AuthContext = React.createContext<IAuthContext>({});

export const AuthContextProvider: React.FC<
    IAuthContext & {
        children?: React.ReactNode;
    }
> = ({ children, isProvided, ...authOperations }) => {
    const { replace } = useNavigation();
    const queryClient = useQueryClient();

    const invalidateAuthStore = () => {
        queryClient.invalidateQueries(["useAuthenticated"]);
        queryClient.invalidateQueries(["getUserIdentity"]);
        queryClient.invalidateQueries(["usePermissions"]);
    };

    const loginFunc = async (params: any) => {
        try {
            const result = await authOperations.login?.(params);

            invalidateAuthStore();
            return Promise.resolve(result);
        } catch (error) {
            return Promise.reject(error);
        }
    };

    const registerFunc = async (params: any) => {
        try {
            const result = await authOperations.register?.(params);

            invalidateAuthStore();
            return Promise.resolve(result);
        } catch (error) {
            return Promise.reject(error);
        }
    };

    const logoutFunc = async (params: any) => {
        try {
            const redirectPath = await authOperations.logout?.(params);

            invalidateAuthStore();

            return Promise.resolve(redirectPath);
        } catch (error) {
            return Promise.reject(error);
        }
    };

    const checkAuthFunc = async (params: any) => {
        try {
            await authOperations.checkAuth?.(params);
            return Promise.resolve();
        } catch (error) {
            if ((error as { redirectPath?: string })?.redirectPath) {
                replace((error as { redirectPath: string }).redirectPath);
            }

            return Promise.reject(error);
        }
    };

    return (
        <AuthContext.Provider
            value={{
                ...authOperations,
                login: loginFunc,
                logout: logoutFunc,
                checkAuth: checkAuthFunc,
                register: registerFunc,
                isProvided,
            }}
        >
            {children}
        </AuthContext.Provider>
    );
};
import React from "react";
import {
    useQuery,
    UseQueryResult,
    UseQueryOptions,
} from "@tanstack/react-query";

import { AuthContext } from "@contexts/auth";
import { IAuthContext } from "../../../interfaces";

export type UseGetIdentityProps<TData> = {
    queryOptions?: UseQueryOptions<TData>;
};

/**
 * `useGetIdentity` calls the `getUserIdentity` method from the {@link https://refine.dev/docs/core/providers/auth-provider `authProvider`} under the hood.
 *
 * @see {@link https://refine.dev/docs/core/hooks/auth/useGetIdentity} for more details.
 *
 * @typeParam TData - Result data of the query
 *
 */
export const useGetIdentity = <TData = any>({
    queryOptions,
}: UseGetIdentityProps<TData> = {}): UseQueryResult<TData, unknown> => {
    const { getUserIdentity } = React.useContext<IAuthContext>(AuthContext);

    const queryResponse = useQuery<TData>(
        ["getUserIdentity"],
        // Enabled check for `getUserIdentity` is enough to be sure that it's defined in the query function but TS is not smart enough to know that.
        getUserIdentity ?? (() => Promise.resolve({})),
        {
            enabled: !!getUserIdentity,
            retry: false,
            ...queryOptions,
        },
    );

    return queryResponse;
};
import React from "react";
import {
    useMutation,
    UseMutationOptions,
    UseMutationResult,
} from "@tanstack/react-query";

import { AuthContext } from "@contexts/auth";
import { IAuthContext, TLogoutData } from "../../../interfaces";
import { useNavigation, useNotification } from "@hooks";

type Variables = {
    redirectPath?: string | false;
};

export type UseLogoutProps<TVariables> = {
    mutationOptions?: Omit<
        UseMutationOptions<
            TLogoutData,
            Error,
            (TVariables & Variables) | void,
            unknown
        >,
        "mutationFn" | "onError" | "onSuccess"
    >;
};

/**
 * `useLogout` calls the `logout` method from the {@link https://refine.dev/docs/api-references/providers/auth-provider `authProvider`} under the hood.
 *
 * @see {@link https://refine.dev/docs/core/hooks/auth/useLogout} for more details.
 *
 */
export const useLogout = <TVariables = {}>({
    mutationOptions,
}: UseLogoutProps<TVariables> = {}): UseMutationResult<
    TLogoutData,
    Error,
    (TVariables & Variables) | void,
    unknown
> => {
    const { push } = useNavigation();
    const { logout: logoutFromContext } =
        React.useContext<IAuthContext>(AuthContext);
    const { open } = useNotification();

    const queryResponse = useMutation<
        TLogoutData,
        Error,
        (TVariables & Variables) | void,
        unknown
    >(["useLogout"], logoutFromContext, {
        onSuccess: (data, variables) => {
            const redirectPath = variables?.redirectPath ?? data;

            if (redirectPath === false) {
                return;
            }

            if (redirectPath) {
                push(redirectPath);
                return;
            }

            push("/login");
        },
        onError: (error: Error) => {
            open?.({
                key: "useLogout-error",
                type: "error",
                message: error?.name || "Logout Error",
                description:
                    error?.message || "Something went wrong during logout",
            });
        },
        ...mutationOptions,
