};
import { IQueryKeys } from "src/interfaces";
import { QueryKey } from "@tanstack/react-query";

import { MetaDataQuery } from "src/interfaces";

export const queryKeys = (
    resource?: string,
    dataProviderName?: string,
    metaData?: MetaDataQuery | undefined,
): IQueryKeys => {
    const providerName = dataProviderName || "default";
    const keys: IQueryKeys = {
        all: [providerName],
        resourceAll: [providerName, resource || ""],
        list: (config) => [
            ...keys.resourceAll,
            "list",
            { ...config, ...metaData } as QueryKey,
        ],
        many: (ids) =>
            [
                ...keys.resourceAll,
                "getMany",
                (ids && ids.map(String)) as QueryKey,
                { ...metaData } as QueryKey,
            ].filter((item) => item !== undefined),
        detail: (id) => [
            ...keys.resourceAll,
            "detail",
            id?.toString(),
            { ...metaData } as QueryKey,
        ],
        logList: (meta) =>
            ["logList", resource, meta as any, metaData as QueryKey].filter(
                (item) => item !== undefined,
            ),
    };
    return keys;
};
export const hasPermission = (
    permissions: string[] | undefined,
    action: string | undefined,
): boolean => {
    if (!permissions || !action) {
        return false;
    }
    return !!permissions.find((i) => i === action);
};
import { ResourceProps } from "src/interfaces";

const getParentOf = (item: ResourceProps) => {
    return (resourceItem: ResourceProps) =>
        item.parentName ? resourceItem.name === item.parentName : false;
};

export const routeGenerator = (
    item: ResourceProps,
    resourcesFromProps: ResourceProps[],
): string | undefined => {
    let route;

    const resourceRoute = item.options?.route ?? item.name;

    if (item.parentName) {
        const parent = resourcesFromProps.find(getParentOf(item));

        if (parent?.parentName) {
            const routePrefix = routeGenerator(parent, resourcesFromProps);

            route = `${routePrefix}/${resourceRoute}`;
        } else if (item.parentName) {
            const parentPrefix =
                parent?.options?.route ?? parent?.name ?? item.parentName;
            route = `${parentPrefix}/${resourceRoute}`;
        }
    } else {
        route = resourceRoute;
    }
    return route;
};
