        UseMutationOptions<
            DeleteOneResponse<TData>,
            TError,
            DeleteParams<TVariables>,
            DeleteContext<TData>
        >,
        "mutationFn" | "onError" | "onSuccess" | "onSettled" | "onMutate"
    >;
