                (resolve, reject) => {
                    const doMutation = () => {
                        dataProvider(
                            pickDataProvider(
                                resource,
                                dataProviderName,
                                resources,
                            ),
                        )
                            .deleteOne<TData, TVariables>({
                                resource,
                                id,
                                metaData,
                                variables: values,
                            })
                            .then((result) => resolve(result))
                            .catch((err) => reject(err));
