    ActionParams &
    LiveModeProps;

export type UseFormProps<
    TData extends BaseRecord = BaseRecord,
    TError extends HttpError = HttpError,
    TVariables = {},
> = ActionFormProps<TData, TError, TVariables> & ActionParams & LiveModeProps;

export type UseFormReturnType<
    TData extends BaseRecord = BaseRecord,
    TError extends HttpError = HttpError,
    TVariables = {},
> = {
    id?: BaseKey;
    setId: Dispatch<SetStateAction<BaseKey | undefined>>;

    queryResult?: QueryObserverResult<GetOneResponse<TData>>;
    mutationResult:
        | UseUpdateReturnType<TData, TError, TVariables>
        | UseCreateReturnType<TData, TError, TVariables>;
    formLoading: boolean;
    onFinish: (
        values: TVariables,
    ) => Promise<CreateResponse<TData> | UpdateResponse<TData> | void>;
    redirect: (
        redirect: RedirectAction,
        idFromFunction?: BaseKey | undefined,
    ) => void;
};

/**
 * `useForm` is used to manage forms. It uses Ant Design {@link https://ant.design/components/form/ Form} data scope management under the hood and returns the required props for managing the form actions.
 *
 * @see {@link https://refine.dev/docs/api-references/hooks/form/useForm} for more details.
 *
 * @typeParam TData - Result data of the query extends {@link https://refine.dev/docs/api-references/interfaceReferences#baserecord `BaseRecord`}
 * @typeParam TError - Custom error object that extends {@link https://refine.dev/docs/api-references/interfaceReferences#httperror `HttpError`}
 * @typeParam TVariables - Values for params. default `{}`
 *
 *
 */
export const useForm = <
    TData extends BaseRecord = BaseRecord,
    TError extends HttpError = HttpError,
    TVariables = {},
>({
    resource: resourceFromProps,
    action: actionFromProps,
    id: idFromProps,
    onMutationSuccess,
    onMutationError,
    redirect: redirectFromProps,
    successNotification,
    errorNotification,
    metaData,
    mutationMode: mutationModeProp,
    liveMode,
    onLiveEvent,
    liveParams,
    undoableTimeout,
    dataProviderName,
    invalidates,
    queryOptions,
    createMutationOptions,
    updateMutationOptions,
}: UseFormProps<TData, TError, TVariables> = {}): UseFormReturnType<
    TData,
    TError,
    TVariables
> => {
    const { options } = useRefineContext();
    const { useParams } = useRouterContext();
    const {
        resource: resourceFromRoute,
        action: actionFromRoute,
        id: idFromParams,
    } = useParams<ResourceRouterParams>();

    const defaultId =
        !resourceFromProps || resourceFromProps === resourceFromRoute
            ? idFromProps ?? idFromParams
            : idFromProps;

    // id state is needed to determine selected record in a context for example useModal
    const [id, setId] = React.useState<BaseKey | undefined>(defaultId);

    React.useEffect(() => {
        if (defaultId !== id) {
            setId(idFromProps);
        }
    }, [idFromProps]);

    const resourceName = resourceFromProps ?? resourceFromRoute;
    const action =
        actionFromProps ??
        (actionFromRoute === "show" ? "create" : actionFromRoute) ??
        "create";

    const resourceWithRoute = useResourceWithRoute();
    const resource = resourceWithRoute(resourceName);

    const { mutationMode: mutationModeContext } = useMutationMode();
    const mutationMode = mutationModeProp ?? mutationModeContext;

    const isCreate = action === "create";
    const isEdit = action === "edit";
    const isClone = action === "clone";

    const redirect = redirectPage({
        redirectFromProps,
        action,
        redirectOptions: options.redirect,
    });

    const enableQuery = id !== undefined && (isEdit || isClone);

    const queryResult = useOne<TData>({
        resource: resource.name,
        id: id ?? "",
        queryOptions: {
            enabled: enableQuery,
            ...queryOptions,
        },
        liveMode,
        onLiveEvent,
        liveParams,
        metaData,
        dataProviderName,
    });

    const { isFetching: isFetchingQuery } = queryResult;

    const mutationResultCreate = useCreate<TData, TError, TVariables>({
        mutationOptions: createMutationOptions,
    });
    const { mutate: mutateCreate, isLoading: isLoadingCreate } =
        mutationResultCreate;

    const mutationResultUpdate = useUpdate<TData, TError, TVariables>({
        mutationOptions: updateMutationOptions,
    });
    const { mutate: mutateUpdate, isLoading: isLoadingUpdate } =
        mutationResultUpdate;

    const { setWarnWhen } = useWarnAboutChange();

    const handleSubmitWithRedirect = useRedirectionAfterSubmission();

    const onFinishCreate = async (values: TVariables) => {
        setWarnWhen(false);

        const onSuccess = (id?: BaseKey) => {
            handleSubmitWithRedirect({
                redirect,
                resource,
                id,
            });
        };

        if (mutationMode !== "pessimistic") {
            setTimeout(() => {
                onSuccess();
            });
        }

        return new Promise<CreateResponse<TData> | void>((resolve, reject) => {
            if (mutationMode !== "pessimistic") {
                resolve();
            }
            return mutateCreate(
                {
                    values,
                    resource: resource.name,
                    successNotification,
                    errorNotification,
                    metaData,
                    dataProviderName,
                    invalidates,
                },
                {
                    onSuccess: (data, _, context) => {
                        if (onMutationSuccess) {
                            onMutationSuccess(data, values, context);
                        }

                        const responseId = data?.data?.id;

                        onSuccess(responseId);

                        resolve(data);
                    },
                    onError: (error: TError, _, context) => {
                        if (onMutationError) {
                            return onMutationError(error, values, context);
                        }
                        reject();
                    },
                },
            );
        });
    };

    const onFinishUpdate = async (values: TVariables) => {
        setWarnWhen(false);

        const variables: UpdateParams<TVariables> = {
            id: id ?? "",
            values,
            resource: resource.name,
            mutationMode,
            undoableTimeout,
            successNotification,
            errorNotification,
            metaData,
            dataProviderName,
            invalidates,
        };

        const onSuccess = () => {
            // If it is in modal mode set it to undefined. Otherwise set it to current id from route.
            setId(defaultId);
            handleSubmitWithRedirect({
                redirect,
                resource,
                id,
            });
        };

        // setWarnWhen is set to "false" at the start of the mutation. With the help of setTimeout we guarantee that the value false is set.
        if (mutationMode !== "pessimistic") {
            setTimeout(() => {
                onSuccess();
            });
        }

        // setTimeout is required to make onSuccess e.g. callbacks to work if component unmounts i.e. on route change
        return new Promise<UpdateResponse<TData> | void>((resolve, reject) => {
            if (mutationMode !== "pessimistic") {
                resolve();
            }
            return setTimeout(() => {
                mutateUpdate(variables, {
                    onSuccess: (data, _, context) => {
                        if (onMutationSuccess) {
                            onMutationSuccess(data, values, context);
                        }

                        if (mutationMode === "pessimistic") {
                            onSuccess();
                        }

                        resolve(data);
                    },
                    onError: (error: TError, _, context) => {
                        if (onMutationError) {
                            return onMutationError(error, values, context);
                        }
                        reject();
                    },
                });
            });
        });
    };

    const createResult = {
        formLoading: isFetchingQuery || isLoadingCreate,
        mutationResult: mutationResultCreate,
        onFinish: onFinishCreate,
    };

    const editResult = {
        formLoading: isFetchingQuery || isLoadingUpdate,
        mutationResult: mutationResultUpdate,
        onFinish: onFinishUpdate,
    };

    const result = isCreate || isClone ? createResult : editResult;

    return {
        ...result,
        queryResult,
        id,
        setId,
        redirect: (redirect, idFromFunction?: BaseKey | undefined) => {
            handleSubmitWithRedirect({
                redirect:
                    redirect !== undefined
                        ? redirect
                        : isEdit
                        ? "list"
                        : "edit",
                resource,
                id: idFromFunction ?? id,
            });
        },
    };
};
import { useCallback } from "react";

import { BaseKey, IResourceItem, RedirectAction } from "../../interfaces";
import { useNavigation } from "@hooks";

export type UseRedirectionAfterSubmissionType = () => (options: {
    redirect: RedirectAction;
    resource: IResourceItem;
    id?: BaseKey;
}) => void;

export const useRedirectionAfterSubmission: UseRedirectionAfterSubmissionType =
    () => {
        const { show, edit, list, create } = useNavigation();

        const handleSubmitWithRedirect = useCallback(
            ({
                redirect,
                resource,
                id,
            }: {
                redirect: RedirectAction;
                resource: IResourceItem;
                id?: BaseKey;
            }) => {
                if (redirect && resource.route) {
                    if (resource.canShow && redirect === "show" && id) {
                        return show(resource.route, id);
                    }

                    if (resource.canEdit && redirect === "edit" && id) {
                        return edit(resource.route, id);
                    }

                    if (resource.canCreate && redirect === "create") {
                        return create(resource.route);
                    }

                    return list(resource.route, "push");
                } else {
                    return;
                }
            },
            [],
        );

        return handleSubmitWithRedirect;
    };
import { useResourceWithRoute, useRouterContext } from "@hooks";
import { BaseKey } from "../../interfaces";

export type HistoryType = "push" | "replace";

/**
 * `refine` uses {@link https://reactrouter.com/web/api/Hooks `React Router`} and comes with all redirects out of the box.
 * It allows you to manage your routing operations in refine.
 * Using this hook, you can manage all the routing operations of your application very easily.
 *
 * @see {@link https://refine.dev/docs/core/hooks/navigation/useNavigation} for more details.
 */
export const useNavigation = () => {
    const { useHistory } = useRouterContext();
    const history = useHistory();
    const resourceWithRoute = useResourceWithRoute();

    const handleUrl = (url: string, type: HistoryType = "push") => {
        type === "push" ? history.push(url) : history.replace(url);
    };

    const createUrl = (resource: string) => {
        const resourceName = resourceWithRoute(resource);
        return `/${resourceName.route}/create`;
    };

    const editUrl = (resource: string, id: BaseKey) => {
        const resourceName = resourceWithRoute(resource);
        const encodedId = encodeURIComponent(id);

        return `/${resourceName.route}/edit/${encodedId}`;
    };

    const cloneUrl = (resource: string, id: BaseKey) => {
        const resourceName = resourceWithRoute(resource);
        const encodedId = encodeURIComponent(id);
        return `/${resourceName.route}/clone/${encodedId}`;
    };

    const showUrl = (resource: string, id: BaseKey) => {
        const resourceName = resourceWithRoute(resource);
        const encodedId = encodeURIComponent(id);
        return `/${resourceName.route}/show/${encodedId}`;
    };

    const listUrl = (resource: string) => {
        const resourceName = resourceWithRoute(resource);
        return `/${resourceName.route}`;
    };

    const create = (resource: string, type: HistoryType = "push") => {
        handleUrl(createUrl(resource), type);
    };

    const edit = (
        resource: string,
        id: BaseKey,
        type: HistoryType = "push",
    ) => {
        handleUrl(editUrl(resource, id), type);
    };

    const clone = (
        resource: string,
        id: BaseKey,
        type: HistoryType = "push",
    ) => {
        handleUrl(cloneUrl(resource, id), type);
    };

    const show = (
        resource: string,
        id: BaseKey,
        type: HistoryType = "push",
    ) => {
        handleUrl(showUrl(resource, id), type);
    };

    const list = (resource: string, type: HistoryType = "push") => {
        handleUrl(listUrl(resource), type);
    };

    const push = (path: string, ...rest: unknown[]) => {
        history.push(path, ...rest);
    };

    const replace = (path: string, ...rest: unknown[]) => {
        history.replace(path, ...rest);
    };

    const goBack = () => {
        history.goBack();
    };

    return {
        create,
        createUrl,
        edit,
        editUrl,
        clone,
        cloneUrl,
        show,
        showUrl,
        list,
        listUrl,
        push,
        replace,
        goBack,
    };
};
