import { useContext } from "react";
import {
    useQuery,
    UseQueryResult,
    UseQueryOptions,
} from "@tanstack/react-query";

import { AuditLogContext } from "@contexts/auditLog";
import { queryKeys } from "@definitions/helpers";
import { HttpError, MetaDataQuery } from "../../../interfaces";

export type UseLogProps<TData, TError> = {
    resource: string;
    action?: string;
    meta?: Record<number | string, any>;
    author?: Record<number | string, any>;
    queryOptions?: UseQueryOptions<TData, TError>;
    metaData?: MetaDataQuery;
};

/**
 * useLogList is used to get and filter audit logs.
 * @see {@link https://refine.dev/docs/core/hooks/audit-log/useLogList} for more details.
 */
export const useLogList = <TData = any, TError extends HttpError = HttpError>({
    resource,
    action,
    meta,
    author,
    metaData,
    queryOptions,
}: UseLogProps<TData, TError>): UseQueryResult<TData> => {
    const { get } = useContext(AuditLogContext);

    const queryKey = queryKeys(resource, undefined, metaData);

    const queryResponse = useQuery<TData, TError>(
        queryKey.logList(meta),
        () =>
            get?.({
                resource,
                action,
                author,
                meta,
                metaData,
            }) ?? Promise.resolve([]),
        {
            enabled: typeof get !== "undefined",
            ...queryOptions,
            retry: false,
        },
    );

    return queryResponse;
};
import { useCallback } from "react";
import { useQueryClient } from "@tanstack/react-query";

import { useResource } from "@hooks/resource";
import { queryKeys, pickDataProvider } from "@definitions";
import { BaseKey, IQueryKeys } from "../../interfaces";

export type UseInvalidateProp = {
    resource?: string;
    id?: BaseKey;
    dataProviderName?: string;
    invalidates: Array<keyof IQueryKeys> | false;
};

export const useInvalidate = (): ((props: UseInvalidateProp) => void) => {
    const { resources } = useResource();
    const queryClient = useQueryClient();

    const invalidate = useCallback(
        ({
            resource,
            dataProviderName,
            invalidates,
            id,
        }: UseInvalidateProp) => {
            if (invalidates === false) {
                return;
            }
            const queryKey = queryKeys(
                resource,
                pickDataProvider(resource, dataProviderName, resources),
            );

            invalidates.forEach((key) => {
                switch (key) {
                    case "all":
                        queryClient.invalidateQueries(queryKey.all);
                        break;
                    case "list":
                        queryClient.invalidateQueries(queryKey.list());
                        break;
                    case "many":
                        queryClient.invalidateQueries(queryKey.many());
                        break;
                    case "resourceAll":
                        queryClient.invalidateQueries(queryKey.resourceAll);
                        break;
                    case "detail":
                        queryClient.invalidateQueries(
                            queryKey.detail(id || ""),
                        );
                        break;
                    default:
                        break;
                }
            });
        },
        [],
    );

    return invalidate;
};
import React, { useContext } from "react";
import warnOnce from "warn-once";

import { useResource, useRouterContext, useTranslate } from "@hooks";
import { TranslationContext } from "@contexts/translation";
import { humanizeString } from "@definitions";

import { ResourceRouterParams } from "../../interfaces";

export type BreadcrumbsType = {
    label: string;
    href?: string;
    icon?: React.ReactNode;
};

type UseBreadcrumbReturnType = {
    breadcrumbs: BreadcrumbsType[];
};

export const useBreadcrumb = (): UseBreadcrumbReturnType => {
    const { useParams } = useRouterContext();
    const { i18nProvider } = useContext(TranslationContext);

    const translate = useTranslate();

    const { resources, resource } = useResource();

    const { action } = useParams<ResourceRouterParams>();

    const breadcrumbs: BreadcrumbsType[] = [];

    if (!resource?.name) {
        return { breadcrumbs };
    }

    const addBreadcrumb = (parentName: string) => {
        const parentResource = resources.find(
            (resource) => resource.name === parentName,
        );

        if (parentResource) {
            if (parentResource.parentName) {
                addBreadcrumb(parentResource.parentName);
            }
            breadcrumbs.push({
                label:
                    parentResource.label ??
                    translate(
                        `${parentResource.name}.${parentResource.name}`,
                        humanizeString(parentResource.name),
                    ),

                href: !!parentResource.list
                    ? `/${parentResource.route}`
                    : undefined,
                icon: parentResource.icon,
            });
        }
    };

    if (resource.parentName) {
        addBreadcrumb(resource.parentName);
    }

    breadcrumbs.push({
        label:
            resource.label ??
            translate(
                `${resource.name}.${resource.name}`,
                humanizeString(resource.name),
            ),
        href: !!resource.list ? `/${resource.route}` : undefined,
        icon: resource.icon,
    });

    if (action) {
        const key = `actions.${action}`;
        const actionLabel = translate(key);
        if (typeof i18nProvider !== "undefined" && actionLabel === key) {
            warnOnce(
                true,
                `[useBreadcrumb]: Breadcrumb missing translate key for the "${action}" action. Please add "actions.${action}" key to your translation file.\nFor more information, see https://refine.dev/docs/core/hooks/useBreadcrumb/#i18n-support`,
            );
            breadcrumbs.push({
                label: translate(`buttons.${action}`, humanizeString(action)),
            });
        } else {
            breadcrumbs.push({
                label: translate(key, humanizeString(action)),
            });
        }
    }

    return {
        breadcrumbs,
    };
};
import React from "react";
import {
    useRefineContext,
    useTranslate,
    useResource,
    useRouterContext,
} from "..";
import { IMenuItem, ITreeMenu } from "../../interfaces";
import { userFriendlyResourceName, createTreeView } from "@definitions";

type useMenuReturnType = {
    defaultOpenKeys: string[];
    selectedKey: string;
    menuItems: ITreeMenu[];
};

/**
 * `useMenu` is used to get menu items of the default sidebar.
 * These items include a link to dashboard page (if it exists) and links to the user defined resources
 * (passed as children to {@link https://refine.dev/docs/core/components/refine-config `<Refine>`}).
 * This hook can also be used to build custom menus, which is also used by default sidebar to show menu items.
 *
 * @see {@link https://refine.dev/docs/core/hooks/ui/useMenu} for more details.
 */
export const useMenu: () => useMenuReturnType = () => {
    const { resources } = useResource();
    const translate = useTranslate();

    const { useLocation, useParams } = useRouterContext();
    const location = useLocation();
    const params = useParams<{ resource: string }>();

    const { hasDashboard } = useRefineContext();

    const selectedKey = React.useMemo(() => {
        let selectedResource = resources.find(
            (el) => location?.pathname === `/${el.route}`,
        );

        if (!selectedResource) {
            selectedResource = resources.find(
                (el) => params?.resource === (el.route as string),
            );
        }

        let _selectedKey: string;
        if (selectedResource?.route) {
            _selectedKey = `/${selectedResource?.route}`;
        } else if (location.pathname === "/") {
            _selectedKey = "/";
        } else {
            _selectedKey = location?.pathname;
        }
        return _selectedKey;
    }, [resources, location, params]);

    const treeMenuItems: IMenuItem[] = React.useMemo(
        () =>
            resources.map((resource) => {
                const route = `/${resource.route}`;

                return {
                    ...resource,
                    icon: resource.icon,
                    route: route,
                    key: resource.key ?? route,
                    label:
                        resource.label ??
                        translate(
                            `${resource.name}.${resource.name}`,
                            userFriendlyResourceName(resource.name, "plural"),
                        ),
                };
            }),
        [resources, hasDashboard, translate],
    );
    const menuItems: ITreeMenu[] = React.useMemo(
        () => createTreeView(treeMenuItems),
        [treeMenuItems],
    );

    const crawlNestedKeys = React.useCallback(
        (
            currentKey: string,
            currentResources: typeof resources,
            isParent = false,
        ): string[] => {
            const currentElement = currentResources.find((el) =>
                isParent ? el.name === currentKey : el.route === currentKey,
            );

            if (currentElement) {
                const keysArray: string[] = [];

                if (isParent && currentElement.route) {
                    keysArray.unshift(...[currentElement.route]);
                }

                if (currentElement.parentName) {
                    keysArray.unshift(
                        ...crawlNestedKeys(
                            currentElement.parentName,
                            currentResources,
                            true,
                        ),
                    );
                }
                return keysArray;
            }

            return [];
        },
        [],
    );

    const defaultOpenKeys = React.useMemo(
        () => crawlNestedKeys(selectedKey, treeMenuItems),
        [selectedKey, treeMenuItems],
    );

    const values = React.useMemo(() => {
        const filterMenuItemsByListViewAndHideOption = (
            menus: ITreeMenu[],
        ): ITreeMenu[] => {
            return menus.reduce((menuItem: ITreeMenu[], obj) => {
                if (obj.children.length > 0 && obj.options?.hide !== true)
                    return [
                        ...menuItem,
                        {
                            ...obj,
                            children: filterMenuItemsByListViewAndHideOption(
                                obj.children,
                            ),
                        },
                    ];
                else if (
                    typeof obj.list !== "undefined" &&
                    obj.options?.hide !== true
                )
                    return [...menuItem, obj];

                return menuItem;
            }, []);
        };

        return {
            defaultOpenKeys,
            selectedKey,
            menuItems: filterMenuItemsByListViewAndHideOption(menuItems),
        };
    }, [defaultOpenKeys, selectedKey, menuItems]);

    return values;
};
import React, { useState } from "react";

import { useLogin, useTranslate } from "@hooks";
export interface ILoginForm {
    username: string;
    password: string;
}

/**
 * @deprecated LoginPage is deprecated. Use AuthPage instead. @see {@link https://refine.dev/docs/core/components/auth-page} for more details.
 * **refine** has a default login page form which is served on `/login` route when the `authProvider` configuration is provided.
 *
 * @see {@link https://refine.dev/docs/api-references/components/refine-config#loginpage} for more details.
 */
export const LoginPage: React.FC = () => {
