    const translate = useTranslate();

    const { mutate: login } = useLogin<ILoginForm>();

    return (
        <>
            <h1>{translate("pages.login.title", "Sign in your account")}</h1>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    login({ username, password });
                }}
            >
                <table>
                    <tbody>
                        <tr>
                            <td>
                                {translate(
