          @deprecated `undoableTimeout` property is deprecated at this level. Use it from within `options` instead.
          @example  `options={{ undoableTimeout: 5000 }}`
          @see https://refine.dev/docs/core/components/refine-config/#undoabletimeout
      *   @type [`number`](/docs/api-reference/core/components/refine-config/#undoabletimeout)
       */
    undoableTimeout?: number;
}

/**
 * {@link https://refine.dev/docs/api-references/components/refine-config `<Refine> component`} is the entry point of a refine app.
 * It is where the highest level of configuration of the app occurs.
 * Only a dataProvider is required to bootstrap the app. After adding a dataProvider, resources can be added as property.
 *
 * @see {@link https://refine.dev/docs/api-references/components/refine-config} for more details.
 */
export const Refine: React.FC<RefineProps> = ({
    authProvider,
    dataProvider,
    routerProvider,
    notificationProvider,
    accessControlProvider,
    auditLogProvider,
    resources: resourcesFromProps,
    DashboardPage,
    ReadyPage,
    LoginPage,
    catchAll,
    children,
    liveProvider,
    i18nProvider,
    mutationMode,
    syncWithLocation,
    warnWhenUnsavedChanges,
    undoableTimeout,
    Title,
    Layout,
    Sider,
    Header,
    Footer,
    OffLayoutArea,
    reactQueryClientConfig,
    reactQueryDevtoolConfig,
    liveMode,
    onLiveEvent,
    disableTelemetry,
    options,
}) => {
    const {
        optionsWithDefaults,
        disableTelemetryWithDefault,
        reactQueryWithDefaults,
    } = handleRefineOptions({
        options,
        disableTelemetry,
        liveMode,
        mutationMode,
        reactQueryClientConfig,
        reactQueryDevtoolConfig,
        syncWithLocation,
        warnWhenUnsavedChanges,
        undoableTimeout,
    });

    const queryClient = useDeepMemo(() => {
        if (reactQueryWithDefaults.clientConfig instanceof QueryClient) {
            return reactQueryWithDefaults.clientConfig;
        }

        return new QueryClient({
            ...reactQueryWithDefaults.clientConfig,
            defaultOptions: {
                ...reactQueryWithDefaults.clientConfig.defaultOptions,
                queries: {
                    refetchOnWindowFocus: false,
                    keepPreviousData: true,
                    ...reactQueryWithDefaults.clientConfig.defaultOptions
                        ?.queries,
                },
            },
        });
    }, [reactQueryWithDefaults.clientConfig]);

    const useNotificationProviderValues = React.useMemo(() => {
        return typeof notificationProvider === "function"
            ? notificationProvider
            : () => notificationProvider ?? ({} as INotificationContext);
    }, [notificationProvider]);

    const notificationProviderContextValues = useNotificationProviderValues();

    const resources: IResourceItem[] = useDeepMemo(() => {
        const _resources: IResourceItem[] = [];

        resourcesFromProps?.forEach((resource) => {
            _resources.push({
                key: resource.key,
                name: resource.name,
                label: resource.options?.label,
                icon: resource.icon,
                route: routeGenerator(resource, resourcesFromProps),
                canCreate: !!resource.create,
                canEdit: !!resource.edit,
                canShow: !!resource.show,
                canDelete: resource.canDelete,
                create: resource.create,
                show: resource.show,
                list: resource.list,
                edit: resource.edit,
                options: resource.options,
                parentName: resource.parentName,
            });
        });

        return _resources;
    }, [resourcesFromProps]);

    if (resources.length === 0) {
        return ReadyPage ? <ReadyPage /> : <DefaultReadyPage />;
    }

    const { RouterComponent = React.Fragment } = routerProvider;

    return (
        <QueryClientProvider client={queryClient}>
            <NotificationContextProvider {...notificationProviderContextValues}>
                <AuthContextProvider
                    {...(authProvider ?? {})}
                    isProvided={Boolean(authProvider)}
                >
                    <DataContextProvider {...dataProvider}>
                        <LiveContextProvider liveProvider={liveProvider}>
                            <RouterContextProvider {...routerProvider}>
                                <ResourceContextProvider resources={resources}>
                                    <TranslationContextProvider
                                        i18nProvider={i18nProvider}
                                    >
                                        <AccessControlContextProvider
                                            {...(accessControlProvider ?? {})}
                                        >
                                            <AuditLogContextProvider
                                                {...(auditLogProvider ?? {})}
                                            >
                                                <UndoableQueueContextProvider>
                                                    <RefineContextProvider
                                                        mutationMode={
                                                            optionsWithDefaults.mutationMode
                                                        }
                                                        warnWhenUnsavedChanges={
                                                            optionsWithDefaults.warnWhenUnsavedChanges
                                                        }
                                                        syncWithLocation={
                                                            optionsWithDefaults.syncWithLocation
                                                        }
                                                        Title={Title}
                                                        undoableTimeout={
                                                            optionsWithDefaults.undoableTimeout
                                                        }
                                                        catchAll={catchAll}
                                                        DashboardPage={
                                                            DashboardPage
                                                        }
                                                        LoginPage={LoginPage}
                                                        Layout={Layout}
                                                        Sider={Sider}
                                                        Footer={Footer}
                                                        Header={Header}
                                                        OffLayoutArea={
                                                            OffLayoutArea
                                                        }
                                                        hasDashboard={
                                                            !!DashboardPage
                                                        }
                                                        liveMode={
                                                            optionsWithDefaults.liveMode
                                                        }
                                                        onLiveEvent={
                                                            onLiveEvent
                                                        }
                                                        options={
                                                            optionsWithDefaults
                                                        }
                                                    >
                                                        <UnsavedWarnContextProvider>
                                                            <RouterComponent>
                                                                {children}
                                                                {!disableTelemetryWithDefault && (
                                                                    <Telemetry />
                                                                )}
                                                                <RouteChangeHandler />
                                                            </RouterComponent>
                                                        </UnsavedWarnContextProvider>
                                                    </RefineContextProvider>
                                                </UndoableQueueContextProvider>
                                            </AuditLogContextProvider>
                                        </AccessControlContextProvider>
                                    </TranslationContextProvider>
                                </ResourceContextProvider>
                            </RouterContextProvider>
                        </LiveContextProvider>
                    </DataContextProvider>
                </AuthContextProvider>
            </NotificationContextProvider>
            {reactQueryWithDefaults.devtoolConfig === false ? null : (
                <ReactQueryDevtools
                    initialIsOpen={false}
                    position="bottom-right"
                    {...reactQueryWithDefaults.devtoolConfig}
                />
            )}
        </QueryClientProvider>
    );
};
import { useEffect } from "react";

import { useTelemetryData } from "@hooks/useTelemetryData";

import { ITelemetryData } from "../../interfaces/telementry";

const encode = (payload: ITelemetryData): string => {
    const stringifyedPayload = JSON.stringify(payload || {});

    if (typeof btoa !== "undefined") {
        return btoa(stringifyedPayload);
    }

    return Buffer.from(stringifyedPayload).toString("base64");
};

export const Telemetry: React.FC<{}> = () => {
    const payload = useTelemetryData();

    useEffect(() => {
        if (typeof window === "undefined" && !Image) {
            return;
        }

        const img = new Image();
        img.src = `https://telemetry.refine.dev/telemetry?payload=${encode(
            payload,
        )}`;
    }, []);

    return null;
};
import { useContext } from "react";

import { AuthContext } from "@contexts/auth";
import { AuditLogContext } from "@contexts/auditLog";
import { LiveContext } from "@contexts/live";
import { RouterContext } from "@contexts/router";
import { DataContext } from "@contexts/data";
import { TranslationContext } from "@contexts/translation";
import { NotificationContext } from "@contexts/notification";
import { AccessControlContext } from "@contexts/accessControl";
import { useResource } from "@hooks/resource";

import { ITelemetryData } from "../../interfaces/telementry";

// It reads and updates from package.json during build. ref: tsup.config.ts
const REFINE_VERSION = "3.101.0";

export const useTelemetryData = (): ITelemetryData => {
    const authContext = useContext(AuthContext);
    const auditLogContext = useContext(AuditLogContext);
    const liveContext = useContext(LiveContext);
    const routerContext = useContext(RouterContext);
    const dataContext = useContext(DataContext);
    const { i18nProvider } = useContext(TranslationContext);
    const notificationContext = useContext(NotificationContext);
    const accessControlContext = useContext(AccessControlContext);
    const { resources } = useResource();

    const auth = authContext.isProvided;

    const auditLog =
        !!auditLogContext.create ||
        !!auditLogContext.get ||
        !!auditLogContext.update;

    const live =
        !!liveContext?.publish ||
        !!liveContext?.subscribe ||
        !!liveContext?.unsubscribe;

    const router =
        !!routerContext.useHistory ||
        !!routerContext.Link ||
        !!routerContext.Prompt ||
        !!routerContext.useLocation ||
        !!routerContext.useParams;

    const data = !!dataContext;

    const i18n =
        !!i18nProvider?.changeLocale ||
        !!i18nProvider?.getLocale ||
        !!i18nProvider?.translate;

    const notification =
        !!notificationContext.close || !!notificationContext.open;

    const accessControl = !!accessControlContext.can;

    return {
        providers: {
            auth,
            auditLog,
            live,
            router,
            data,
            i18n,
            notification,
            accessControl,
        },
        version: REFINE_VERSION,
        resourceCount: resources.length,
    };
};
import React, { useMemo } from "react";
import { useMemoized } from "@hooks/memoized";

/**
 * Hook that memoizes the given dependency array and checks the consecutive calls with deep equality and returns the same value as the first call if dependencies are not changed.
 * @internal
 */
export const useDeepMemo = <T,>(
    fn: () => T,
    dependencies: React.DependencyList,
): T => {
    const memoizedDependencies = useMemoized(dependencies);

    const value = useMemo(fn, memoizedDependencies);

    return value;
};
import { useRef } from "react";
import isEqual from "lodash-es/isEqual";

/**
 * Hook that memoizes the given value with deep equality.
 * @internal
 */
export const useMemoized = <T = unknown,>(value: T): T => {
    const ref = useRef(value);

    if (!isEqual(ref.current, value)) {
        ref.current = value;
    }

    return ref.current;
};
import React, { useEffect, useState } from "react";

import { ActionTypes } from "@contexts/undoableQueue";
import { useCancelNotification, useNotification, useTranslate } from "@hooks";
import { IUndoableQueue } from "../../interfaces";

import { userFriendlySecond } from "@definitions/helpers";

export const UndoableQueue: React.FC<{
    notification: IUndoableQueue;
}> = ({ notification }) => {
    const translate = useTranslate();

    const { notificationDispatch } = useCancelNotification();
    const { open } = useNotification();

    const [timeoutId, setTimeoutId] = useState<number | undefined>();

    const cancelNotification = () => {
        if (notification.isRunning === true) {
            if (notification.seconds === 0) {
                notification.doMutation();
            }
            if (!notification.isSilent) {
                open?.({
                    key: `${notification.id}-${notification.resource}-notification`,
                    type: "progress",
                    message: translate(
                        "notifications.undoable",
                        {
                            seconds: userFriendlySecond(notification.seconds),
                        },
                        `You have ${userFriendlySecond(
                            notification.seconds,
                        )} seconds to undo`,
                    ),
                    cancelMutation: notification.cancelMutation,
                    undoableTimeout: userFriendlySecond(notification.seconds),
                });
            }

            if (notification.seconds > 0) {
                if (timeoutId) {
                    clearTimeout(timeoutId);
                }

                const newTimeoutId = setTimeout(() => {
                    notificationDispatch({
                        type: ActionTypes.DECREASE_NOTIFICATION_SECOND,
                        payload: {
                            id: notification.id,
                            seconds: notification.seconds,
                            resource: notification.resource,
                        },
                    });
                }, 1000) as unknown as number;

                setTimeoutId(newTimeoutId);
            }
        }
    };

    useEffect(() => {
        cancelNotification();
    }, [notification]);

    return null;
};
