export function useDataGrid<
    TData extends BaseRecord = BaseRecord,
    TError extends HttpError = HttpError,
    TSearchVariables = unknown,
>({
    columns,
    onSearch: onSearchProp,
    initialCurrent,
    initialPageSize = 25,
    hasPagination = true,
    initialSorter,
    permanentSorter,
    defaultSetFilterBehavior = "replace",
    initialFilter,
    permanentFilter,
    syncWithLocation: syncWithLocationProp,
    resource: resourceFromProp,
    successNotification,
    errorNotification,
    queryOptions,
    liveMode: liveModeFromProp,
    onLiveEvent,
    liveParams,
    metaData,
    dataProviderName,
}: UseDataGridProps<TData, TError, TSearchVariables> = {}):
    | UseDataGridReturnType<TData, TError, TSearchVariables>
    | UseDataGridNoPaginationReturnType<TData, TSearchVariables>
    | UseDataGridWithColumnsReturnType<TData, TError, TSearchVariables>
    | UseDataGridWithColumnsNoPaginationReturnType<
          TData,
          TError,
          TSearchVariables
      > {
    const [columnsTypes, setColumnsType] = useState<Record<string, string>>();

    const {
        tableQueryResult,
        current,
        setCurrent,
        pageSize,
        setPageSize,
        filters,
        setFilters,
        sorter,
        setSorter,
        pageCount,
        createLinkForSyncWithLocation,
    } = useTableCore<TData, TError>({
        permanentSorter,
        permanentFilter,
        initialCurrent,
        initialPageSize,
        // @ts-expect-error currently boolean casting is not supported in overloaded types.
        hasPagination: hasPagination,
        initialSorter,
        initialFilter,
        syncWithLocation: syncWithLocationProp,
        defaultSetFilterBehavior,
        resource: resourceFromProp,
        successNotification,
        errorNotification,
        queryOptions,
        liveMode: liveModeFromProp,
        onLiveEvent,
        liveParams,
        metaData,
        dataProviderName,
    });

    const [muiCrudFilters, setMuiCrudFilters] = useState<CrudFilters>(filters);

    useEffect(() => {
        warnOnce(
            !!columns,
            "[useDataGrid]: `columns` is deprecated and will be removed in the next major version.\nFor more information, see https://github.com/refinedev/refine/pull/2072",
        );
    }, []);

    const theme = useTheme();

    const { data, isFetched, isLoading } = tableQueryResult;

    const liveMode = useLiveMode(liveModeFromProp);

    const handlePageChange = (page: number) => {
        if (hasPagination) {
            setCurrent(page + 1);
        }
    };
    const handlePageSizeChange = (pageSize: number) => {
        if (hasPagination) {
            setPageSize(pageSize);
        }
    };

    const handleSortModelChange = (sortModel: GridSortModel) => {
        const crudSorting = transformSortModelToCrudSorting(sortModel);
        setSorter(crudSorting);
    };

    const handleFilterModelChange = (filterModel: GridFilterModel) => {
        const crudFilters = transformFilterModelToCrudFilters(filterModel);
        setMuiCrudFilters(crudFilters);
        setFilters(crudFilters.filter((f) => f.value !== ""));
        if (hasPagination) {
            setCurrent(1);
        }
    };

    const search = async (value: TSearchVariables) => {
        if (onSearchProp) {
            const searchFilters = await onSearchProp(value);
            setMuiCrudFilters(searchFilters);
            setFilters(searchFilters.filter((f) => f.value !== ""));
            if (hasPagination) {
                setCurrent(1);
            }
        }
    };

    const paginationValues = useMemo(() => {
        if (hasPagination) {
            return {
                current,
                setCurrent,
                pageSize,
                setPageSize,
                pageCount,
            };
        }

        return {
            current: undefined,
            setCurrent: undefined,
            pageSize: undefined,
            setPageSize: undefined,
            pageCount: undefined,
        };
    }, [hasPagination, current, pageSize, pageCount]);

    const dataGridPaginationValues = () => {
        if (hasPagination) {
            return {
                paginationMode: "server" as const,
                page: (current ?? 1) - 1,
                onPageChange: handlePageChange,
                pageSize: pageSize,
                onPageSizeChange: handlePageSizeChange,
            };
        }

        return {
            hideFooterPagination: true,
